import React from "react";
import "../bulma/css/bulma.min.css";
import "../App.css";
import { useEffect } from "react";
import { useState } from "react";
//import { SvgXml } from "react-native-svg";

function TrustedLeadingBrandsAPI() {
  const parse = require("html-react-parser");

  const [data, setData] = useState([]);

  const apiGet = () => {
    fetch("http://127.0.0.1:5000/api_get_trusted_leading_brands")
      .then((respose) => respose.json())
      .then((json) => {
        console.log(json);
        setData(json);
      });
  };

  useEffect(() => {
    apiGet();
  }, []);

  const myData = data;

  const ascendingEvent = () => {
    let data = [...myData];
    if (data.length > 0) {
      let result = data.sort((a, b) =>
        a.company_name_tb01.localeCompare(b.company_name_tb01)
      );
      setData(result);
    }
  };

  const descendingEvent = () => {
    let data = [...myData];
    if (data.length > 0) {
      let result = data.sort((a, b) =>
        b.company_name_tb01.localeCompare(a.company_name_tb01)
      );
      setData(result);
    }
  };

  const sectorAll = () => {
    const result = { apiGet };
    setData(result);
    /*
    let data = [...myData];
    if (data.length > 0) {
      let result = data.sort((a, b) =>
        a.company_name_tb01.localeCompare(b.company_name_tb01)
      );

    }
    */
  };

  const sectorPrimary = () => {
    let data = [...myData];
    if (data.length > 0) {
      const result = data.find(
        (word) => word.economic_sector_tb01 === "Primary"
      );
      console.log(Array(result));
      setData(Array(result));
    }
  };

  const sectorSecondary = () => {
    let data = [...myData];
    if (data.length > 0) {
      let result = data.sort((a, b) =>
        b.company_name_tb01.localeCompare(a.company_name_tb01)
      );
      setData(result);
    }
  };

  const sectorTertiary = () => {
    let data = [...myData];
    if (data.length > 0) {
      let result = data.sort((a, b) =>
        b.company_name_tb01.localeCompare(a.company_name_tb01)
      );
      setData(result);
    }
  };

  return (
    <div>
      <div class="columns FilterContainer">
        <div class="column is-one-fifth">
          <p className="SortFilterText">Filter by: </p>
          <div class="dropdown is-hoverable">
            <div class="dropdown-trigger">
              <button
                class="button"
                aria-haspopup="true"
                aria-controls="dropdown-menu3"
              >
                <span>Select an option</span>
                <span class="icon is-small">
                  <i class="fas fa-angle-down" aria-hidden="true"></i>
                </span>
              </button>
            </div>
            <div class="dropdown-menu" id="dropdown-menu3" role="menu">
              <div class="dropdown-content">
                <button class="dropdown-item is-active" onClick={sectorAll}>
                  All
                </button>
                <button class="dropdown-item" onClick={sectorPrimary}>
                  Sector -- Primary
                </button>
                <button class="dropdown-item" onClick={sectorSecondary}>
                  Sector -- Secondary
                </button>
                <button class="dropdown-item" onClick={sectorTertiary}>
                  Sector -- Tertiary
                </button>
              </div>
            </div>
          </div>
        </div>
        <div class="column is-one-fifth">
          <p className="SortFilterText">Sort by Company Name: </p>
          <button
            class="button is-rounded"
            className="btn_sort"
            onClick={ascendingEvent}
          >
            <span>Ascending</span>
          </button>
          <button
            class="button is-rounded"
            className="btn_sort"
            onClick={descendingEvent}
          >
            <span>Descending</span>
          </button>
        </div>
      </div>
      <div class="TrustedBrandsIconContainer">
        {myData.map((myData) => (
          <div className="TrustedBrandsIcon">
            {parse(myData.brand_image_tb01)}
          </div>
        ))}
      </div>
    </div>
  );

  /*
  return (
    <div>
      <div class="TrustedBrandsIconContainer">
        {myData.map((myData) => (
          <div className="TrustedBrandsIcon">
            {parse(myData.brand_image_tb01)}
          </div>
        ))}
      </div>
    </div>
  );
  */
}

export default TrustedLeadingBrandsAPI;

