import React from "react";
import "../bulma/css/bulma.min.css";
import "../App.css";
import { useEffect } from "react";
import { useState } from "react";
//import { SvgXml } from "react-native-svg";

function TrustedLeadingBrandsAPI() {
  const parse = require("html-react-parser");

  const [data, setData] = useState([]);

  const apiGet = () => {
    fetch("http://127.0.0.1:5000/api_get_trusted_leading_brands")
      .then((respose) => respose.json())
      .then((json) => {
        console.log(json);
        data(json);
      });
  };

  useEffect(() => {
    apiGet();
  }, []);

  let FilterBrandsButton = (props) => {
    function onFilterValueChanged(event) {
      props.filterValueSelected(event.target.value);
    }

    return (
      <div>
        <div className="FilterCSS">
          <select
            name="trusted_leading_brands_tb01"
            onChange={onFilterValueChanged}
          >
            <option value="All">All</option>
            <option value="Primary">Filter by Sector: Primary</option>
            <option value="Secondary">Filter by Sector: Secondary</option>
            <option value="Tertiary">Filter by Sector: Tertiary</option>
          </select>
        </div>
      </div>
    );
  };

  let [onFilterValueChanged, updateFilterText] = useState("All");

  let filteredBrandList = (filterValue) => {
    if (filterValue === "Primary") {
      return (
        <div>
          {setData
            .filter((setData) => setData.id_tb01 > 0)
            .map((setData) => (
              <li>{setData.brand_image_tb01}</li>
            ))}
        </div>
      );
    } else if (filterValue === "Secondary") {
      return <div>Hello World!</div>;
    } else if (filterValue === "Tertiary") {
      return setData.economic_sector_tb01 === "Tertiary";
    } else {
      return setData;
    }
  };

  function onFilterValueSelected(filterValue) {
    updateFilterText(filterValue);
    console.log(filterValue);
  }

  return (
    <div>
      <div class="TrustedBrandsIconContainer">
        <h2>Sort and Filter component</h2>
        <div>
          <FilterBrandsButton filterValueSelected={onFilterValueSelected} />
          <div filteredBrandList={filteredBrandList} />
          <div />
        </div>
        <div>
          {data
            .filter((data) => data.id_tb01 > 0)
            .map((data) => (
              <li>{data.brand_image_tb01}</li>
            ))}
        </div>
        {data.map((data) => (
          <div className="TrustedBrandsIcon">
            {parse(data.brand_image_tb01)}
          </div>
        ))}
      </div>
    </div>
  );

  /*
  return (
    <div>
      <div>
        MY API
        <br />
      </div>
      <div>
        {data.map((item) => (
          <div>
            <Convert string={item.brand_image_tb01} />
          </div>
        ))}
      </div>
      <div></div>
    </div>
  );*/
}

export default TrustedLeadingBrandsAPI;

